package collection;

import java.util.ArrayList;

public class For {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		ArrayList<String>list=new ArrayList<String>();
		list.add("Finla");
		list.add("Meera");
		list.add("Joffin");
		for(String fruit:list)
			System.out.println(fruit);
	}

}
